CREATE PROCEDURE [xls25].[usp_form_dimensions_insert]
      @form_id int = NULL
    , @dimension_id tinyint = NULL
    , @axis_type_id tinyint = NULL
    , @parameter_index tinyint = NULL
    , @header_order tinyint = NULL
    , @root_member_id int = NULL
